import { useState } from "react";
import { Move } from "../types";


interface MovesProps {
    moves: Move[];
    onMoveClick: (move: Move) => Promise<void>
}

export function Moves({ moves, onMoveClick }: MovesProps): JSX.Element {
    const [currentMove, setCurrentMove] = useState(0);

    return (
        <div style={{  }}>
            <div style={{
                flex: 1,
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
                width: "100%"
            }}>
                {moves.map((move, i): JSX.Element => {
                    console.log({i, currentMove})
                    // TODO hightlight current move + hotkey left and right to move from move to move
                    return (
                        <div key={i} style={{ width: "50%", cursor: "pointer", textAlign: "left", backgroundColor: i === currentMove? "#8b8987": "" }} onClick={async () => {
                            setCurrentMove(i);
                            await onMoveClick(move)
                        }}>
                            {move.number}. {move.to} [{move.eval}]
                        </div>
                    );
                })}
            </div>
        </div>
    )
}
