import { useCallback, useMemo, useState } from "react";

import { Chess } from 'chess.js';
import { Line, Move, NewMove } from "../types";
import { engineEval } from "./engine";
import { Lines } from "./lines";
import { Moves } from "./moves";

declare const colors: readonly ["white", "black"];
declare type Color = typeof colors[number];

interface RightMenuProps {
    chess: Chess;

    setFen: React.Dispatch<React.SetStateAction<string>>;

    orientation: Color;
    setOrientation: React.Dispatch<React.SetStateAction<Color>>;
}

export function RightMenu({chess, setFen, orientation, setOrientation }: RightMenuProps): JSX.Element {
    const [moves, setMoves] = useState<Move[]>([]);
    const [lines, setLines] = useState<Line[]>([]);
    const [loading, setLoading] = useState<string|null>(null);

    // Load a game
    const onPGNChange = useCallback(async (pgn: string) => {
        chess.loadPgn(pgn);
        setFen(chess.fen());

        const moves: Move[] = [];
        let index = 0;

        const nbMoves = chess.history().length;

        // start computing every move.
        for (const value of chess.history({ verbose: true })) {
            let _index = index;

            moves.push(NewMove(value, Math.floor(_index / 2) + 1))

            setLoading(`${index}/${nbMoves}`);
            index += 1;
        }
        setMoves(moves);
        setLoading(null);

    }, [chess, setFen]);

    // Load a move
    const onMoveClick = useCallback(async (move: Move) => {
        setFen(move.fen);
        chess.load(move.fen);
        setLines([]);

        await engineEval(move.fen, 3).then((lines) => setLines(lines));
    }, [chess, setFen]);


    useMemo(() => {

        engineEval('r4rk1/1pp2ppp/p1nqp1b1/3pN1P1/3P1P1P/3B1P2/PPPQ4/R3K2R b KQ - 1 14', 3).then(console.log)
        engineEval('r4rk1/1pp2ppp/p2qp1b1/3pN1P1/3n1P1P/3B1P2/PPPQ4/R3K2R w KQ - 0 15', 3).then(console.log)
    }, [])

    return (
        <div>
            <div style={{ flex: 1, border: "1px solid white" }}>
                {/* Fen: <input value={fen} onChange={e => setFen(e.target.value)} /> */}
                PGN: <textarea onChange={async (e) => await onPGNChange(e.target.value)} />
                Direction: <input type="checkbox" checked={orientation === "white"} onChange={e => {console.log(e.target.value); setOrientation(e.target.checked ? "white": "black")}} />
            </div>
            <div style={{ marginTop: 8, height: 700}}>
                {
                    loading ?
                    <div>Analyzing the game {loading}</div>:
                    <>
                        <Lines lines={lines} />
                        <Moves moves={moves} onMoveClick={onMoveClick} />
                    </>
                }
            </div>            
        </div>
    )
}
