import { Line } from "../types";

export const ENGINE_DEPTH = 10;


const META = {
    engine: null
}

export function getEngine() {
    if (META.engine) {
        return META.engine
    }

    META.engine = eval("stockfish");

    META.engine.onmessage = (event: {data: string}) => {
        let message = event.data;
        console.log(message)
    }

    META.engine.onerror = (event: any) => {
        console.log({event});
    }

    // set number of lines to eval
    console.log(META.engine.postMessage("setoption name MultiPV value 3"))

    return META.engine;
}

export async function searchFromFen(fen: string): Promise<Line> {
    return new Promise((resolve, reject) => {
        const engine = getEngine();

        engine.postMessage(`position fen ${fen}`);
        engine.postMessage(`go depth ${ENGINE_DEPTH}`);

        engine.onmessage = (event: {data: string}) => {
            let message = event.data;
            if (message.startsWith(`info depth ${ENGINE_DEPTH}`)) {
                const regx = message.match(`.*score (?<type>cp|mate) (?<score>.*) nodes.* pv (?<moves>.*)`);

                if (regx && regx.groups !== undefined) {
                    resolve({
                        score: Number(regx?.groups?.score) || 0,
                        scoreType: regx?.groups?.type || "",
                        line: regx?.groups?.moves || "",
                    })
                } 
            }
        }
    });
}
