import { Move as cMove } from 'chess.js';
import { engineEval } from './right-menu/engine';

export function NewLine(rawScore, scoreType, line): Line {
    const isMate = scoreType === "mate";
    const score = isMate ? rawScore : rawScore / 100;

    return {
        score: `${isMate ? "M" : ""}${score}`, // in pawns
        rawScore: rawScore, // in centipawns
        scoreType:scoreType,
        line: line,
    } 
}
export function NewMove(value: cMove, moveNumber): Move {
    return {
        to: value.to,
        fen: value.after,
        number: moveNumber,
        eval: '',
        accuracy: 0,
    }
}

export async function ComputeMoveScore(move: Move) {
    await engineEval(value.before, 3).then(async (linesBefore) => {
        await engineEval(value.after, 3).then((linesAfter) => {
            let accuracy = 0;
            let evaluation = 0;

            if (linesBefore.length > 0 && linesAfter.length > 0) {
                const bestLineBefore = linesBefore[0];
                const bestLineAfter = linesAfter[0];

                const winPercentBefore = 2 / (1 + Math.exp(-0.00368208 * bestLineBefore.rawScore)) - 1;
                const winPercentAfter = 2 / (1 + Math.exp(-0.00368208 * bestLineAfter.rawScore)) - 1;

                if (winPercentAfter > winPercentBefore) {
                    accuracy = 100
                } else {
                    accuracy = Math.round(103.1668100711649 * Math.exp(-0.04354415386753951 * (winPercentBefore - winPercentAfter)) -3.166924740191411);
                }
                console.log({x: move.number, linesBefore, bestLineBefore, winPercentBefore, linesAfter, bestLineAfter, winPercentAfter, accuracy})

                evaluation = bestLineBefore.rawScore - bestLineAfter.rawScore;
                // if (evaluation < 0) {
                // }
            }

            // TODO think more about the evals I want
            // I need to compare the score AFTER the move VS the best line BEFORE the move (+all other metrics)
            // If I want a graph, then I want the best score BEFORE each move. 

            move.eval = `${evaluation}` // (linesBefore.length > 0 ? linesBefore[0].score : "-")
            move.accuracy = accuracy
        });         
    });

}

export interface Line {
    line: string;

    scoreType: string;

    rawScore: number;
    score: string;
}

export interface Move {
    number: number;
    to: string;
    fen: string;

    cmove?: cMove;
    eval?: string;
    accuracy?: number;

}
