import { useCallback, useMemo, useState } from "react";

import Chessground from "@react-chess/chessground";
import { Chess, Move as CMove } from 'chess.js';

declare const colors: readonly ["white", "black"];
declare type Color = typeof colors[number];

interface Move {
    number: number;

    to: string;
    fen: string;
}

interface RightMenuProps {
    chess: Chess;

    setFen: React.Dispatch<React.SetStateAction<string>>;

    orientation: Color;
    setOrientation: React.Dispatch<React.SetStateAction<Color>>;
}

function RightMenu({chess, setFen, orientation, setOrientation }: RightMenuProps): JSX.Element {
    const [moves, setMoves] = useState<Move[]>([]);

    const engine = useMemo(() => {console.log("getting sf"); return eval("stockfish")}, []);

    engine.onmessage = (event: {data: string}) => {
        let message = event.data;

        if (message.startsWith('info depth 10')) {
            console.log({ message });
        }
    }
    engine.onerror = (event: any) => {
        let error = event.data ? event.data : event;
        console.log({error});
    }


    // Load a game
    const onPGNChange = useCallback((pgn: string) => {
        chess.loadPgn(pgn);
        setFen(chess.fen());
        console.log({ chess });

        // start computing every move.
        setMoves(chess.history({ verbose: true }).map((value: CMove, index: number): Move => {
            return { to: value.to, fen: value.after, number: Math.floor(index / 2) };
        }));
    }, [chess, setFen]);

    return (
        <div style={{  }}>
            <div style={{ flex: 1 }}>
                {/* Fen: <input value={fen} onChange={e => setFen(e.target.value)} /> */}
                PGN: <textarea onChange={e => onPGNChange(e.target.value)} />
                Direction: <input type="checkbox" checked={orientation === "white"} onChange={e => {console.log(e.target.value); setOrientation(e.target.checked ? "white": "black")}} />
            </div>

            <div style={{overflowY:"auto", height: 800, paddingTop: 8}}>
            <div style={{
                flex: 1,
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
                width: "100%"
                }}>
                    {moves.map((move, i): JSX.Element => {
                        // TODO hightlight current move
                    return (
                        <div key={i}  style={{ width: "50%", cursor: "pointer", textAlign: "left" }} onClick={() => {
                            setFen(move.fen);
                            chess.load(move.fen);

                            engine.postMessage(`position fen ${move.fen}`);
                            engine.postMessage(`go depth 10`);
                        }}>
                            {move.number}. {move.to}
                        </div>
                    );
                })
                }
                </div>
                </div>
        </div>
    )
}

interface ChessUXProps {}
export function ChessUX({ }: ChessUXProps): JSX.Element {
    const [orientation, setOrientation] = useState<Color>("white");
    const [fen, setFen] = useState('');

    const chess = useMemo(() => new Chess(), []);


    return (
        <div style={{ display: 'flex', flexDirection: 'row' }}>
            <div style={{ flex: 2 }}>
                <Chessground config={{
                    fen: fen,
                    orientation: orientation,
                    autoCastle: true,
                    highlight: {
                        lastMove: true,
                        check: true,
                    },
                    animation: {
                        enabled: true,
                    },
                    drawable: {
                        enabled: true,
                    },
                }} />
            </div>
            <RightMenu chess={chess} orientation={orientation} setOrientation={setOrientation} setFen={setFen} />
    </div>)
};
