import { Mutex } from "async-mutex";
import { useMemo, useState } from "react";
import { ComputeMoveScore, Move } from "../types";

interface MoveProps {
    i: number;

    move: Move;
    onMoveClick: (move: Move) => Promise<void>

    currentMove: number;
    setCurrentMove: React.Dispatch<React.SetStateAction<number>>
}

const mutex = new Mutex();

function MoveUX({ i, move, onMoveClick, currentMove, setCurrentMove }: MoveProps): JSX.Element {    
    const [evaluation, setEval] = useState(move.eval);

    useMemo(async () => {
        if (move.eval || evaluation) {
            setEval(move.eval || evaluation);
        }

        const release = await mutex.acquire();
        await ComputeMoveScore(move).then((_move) => {
            setEval(_move.eval);

            release();
        })
     }, []);

    return (
        <div style={{ width: "50%", cursor: "pointer", textAlign: "left", backgroundColor: i === currentMove? "#8b8987": "" }} onClick={async () => {
            setCurrentMove(i);
            await onMoveClick(move)
        }}>
            {move.number}. {move.to} [{evaluation? evaluation: "loading"}]
        </div>
    );
}


interface MovesProps {
    moves: Move[];
    onMoveClick: (move: Move) => Promise<void>
}

export function Moves({ moves, onMoveClick }: MovesProps): JSX.Element {
    const [currentMove, setCurrentMove] = useState(0);

    return (
        <div style={{ overflowY: "auto", height: 600, marginTop: 8, border: "1px solid white",  }}>
            <div style={{
                flex: 1,
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
                width: "100%"
            }}>
                {moves.map((move, i): JSX.Element => {
                    // TODO hightlight current move + hotkey left and right to move from move to move
                    return (
                        <MoveUX key={i} i={i} move={move} onMoveClick={onMoveClick} currentMove={currentMove} setCurrentMove={setCurrentMove}/>
                    );
                })}
            </div>
        </div>
    )
}
